import React, { useState } from 'react';
import './MessageInput.css';

function MessageInput({ onSendMessage, disabled }) {
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim() && !disabled) {
      onSendMessage(message);
      setMessage('');
    }
  };

  const handleQuickAction = (actionText) => {
    setMessage(actionText);
  };

  const quickActions = [
    { id: 1, text: "FAQ: About this website" },
    { id: 2, text: "Search for clinics" },
    { id: 3, text: "Search for services" },
    { id: 4, text: "Search for bookings" },
    { id: 5, text: "Create booking" },
    { id: 6, text: "Price comparison" }
  ];

  return (
    <div className="message-input-container">
      <div className="quick-actions">
        {quickActions.map(action => (
          <button 
            key={action.id}
            className="quick-action-button"
            onClick={() => handleQuickAction(action.text)}
          >
            {action.text}
          </button>
        ))}
      </div>
      <form className="message-input-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          disabled={disabled}
          className="message-input"
        />
        <button type="submit" disabled={disabled || !message.trim()} className="send-button">
          Send
        </button>
      </form>
    </div>
  );
}

export default MessageInput;
